    <div className="container page-content">
        <h4> Podaj dane:</h4>
        <hr></hr>
        <form method="post">
            <input type="text" placeholder="nazwa użytkownika" 
            name="username"
            onChange={(e) => { setUsername(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="hasło" 
            name="password"
            onChange={(e) => 
              { setPassword(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="email" 
            name="email"
            onChange={(e) => 
              { setEmail(e.target.value);
            }}
            ></input><br></br>

            <button onClick={selectUsers}> KLIKNIJ </button>
            {/* <h3> TAJNE DANE </h3> */}
            {usersList.map((val) =>{
              
              return <p> Login: {val.username} | Hasło: {val.password} </p>  /* wersja zabezpieczona */
