import React, {useEffect, useState} from "react";
import './App.css';
import Axios from 'axios';

function Categories() {

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [icon, setIcon] = useState("");

  const [movieCategoriesList, setCategoryList] = useState([]);
  const [CategoryData, setCategoryData] = useState([]);

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/getCategories').then((response)=>
    {
      setCategoryList(response.data);
    }
  )},[])

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/ofertselect').then((response)=>
    {
      setCategoryData(response.data);
    }
  )},[])

  const submitReviewCategories = () => {
      Axios.post("http://localhost:4000/api/insertCategories", {
        name: name,
        description: description,
        icon: icon,
      }).then(() => {
        alert("successful insert category");
      });
  };
  return (
    <div className="container page-content">
        <h4> Wpisz nowe kategorie:</h4>
        <hr></hr>
        <form method="post">
            <input type="text" placeholder="nazwa kategorii" 
            name="name"
            onChange={(e) => { setName(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="opis" 
            name="description"
            onChange={(e) => 
              { setDescription(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="ikona font-awesome" 
            name="icon"
            onChange={(e) => 
              { setIcon(e.target.value);
            }}
            ></input><br></br>
        </form>
            <button onClick={submitReviewCategories}> KLIKNIJ </button>
            {movieCategoriesList.map((val) =>{
              return <div><h4><a href={val.name}>Kategoria: {val.name}</a></h4><p> Opis: {val.description} </p></div>
            })}
        <br></br>
        {CategoryData.map((val) =>{

          return <p> Login: {CategoryData.title} | Hasło: {CategoryData.description} </p>  /* wersja zabezpieczona */
          // return <div><p> Login: <span dangerouslySetInnerHTML={{"__html": val.username}} /> | Hasło <span dangerouslySetInnerHTML={{"__html": val.password}} /></p></div>  /* wersja niezabezpieczona, kod HTML w dangerouslySetInnerHTML */
          // return <div><p><a href={val.username}>Nazwa użytkownika = {val.username}. Akapit przekierowuje do ścieżki twojego użytkownika </a></p></div>; /* wersja niezabezpieczona, kod javascript w atrybucie href znacznika a*/
        })}
    </div>
  );
}

export default Categories;
