import React, {useState} from "react";
import './App.css';
import Axios from 'axios';

function OfertAdd() {

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [amount, setAmount] = useState("");
  const [datetime, setDatetime] = useState("");

  const [CategoryData, setCategoryData] = useState([]);

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/ofertselect').then((response)=>
    {
      setCategoryData(response.data);
    }
  )},[])

  const submitAddOfert = () => {
      Axios.post("http://localhost:4000/api/ofertadd", {
        title: title,
        description: description,
        price: price,
        amount: amount,
        datetime: datetime,
      }).then(() => {
        alert("successful insert ofert");
      });
  };
  return (
    <div className="container page-content">
      <hr className="first"></hr>
      <h2> Dodawanie oferty </h2>
      
      <form className="content" method="post">
          {/* <p className="row"><label>Wybierz kategorie: </label>
          <select name="category" >
          
          </select></p> */}
          <section className="row"> <p> Wpisz tytuł </p> <input type="text" name = "title" onChange={(e) => { setTitle(e.target.value);
            }} 
            ></input> </section>
          <section className="row"> <p> Wpisz opis </p> <textarea rows="11"  name="description" onChange={(e) => { setDescription(e.target.value);
            }}></textarea> </section>
          <section className="row"> <p> Wpisz cene </p> <input id="money" type="number" name="price"  title="Podaj walute" placeholder="wpisz liczbę groszy" onChange={(e) => { setPrice(e.target.value);
            }} required></input></section>
          <section className="row"> <p> Wpisz liczbe produktów </p> <input type="number" name="amount" onChange={(e) => { setAmount(e.target.value);
            }}></input> </section>
          <section className="row"> <p> Wybierz date zakończenia </p> <input type="date" name="datetime" onChange={(e) => { setDatetime(e.target.value);
            }}></input></section>
          
          <input type="submit" name="submit" onClick={submitAddOfert }></input>
      </form>
      {CategoryData.map((val) =>{
          
          return <p> Login: {CategoryData.title} | Hasło: {CategoryData.description} </p>  /* wersja zabezpieczona */
          // return <div><p> Login: <span dangerouslySetInnerHTML={{"__html": val.username}} /> | Hasło <span dangerouslySetInnerHTML={{"__html": val.password}} /></p></div>  /* wersja niezabezpieczona, kod HTML w dangerouslySetInnerHTML */
          // return <div><p><a href={val.username}>Nazwa użytkownika = {val.username}. Akapit przekierowuje do ścieżki twojego użytkownika </a></p></div>; /* wersja niezabezpieczona, kod javascript w atrybucie href znacznika a*/
        })}
      </div>)

}

export default OfertAdd;
