import React, {useEffect, useState} from "react";
import './../App.css';
import Axios from 'axios';

function CategoriesSelect() {

    const [movieCategoriesList, setCategoryList] = useState([]);
  
    useEffect(()=> {
    Axios.get('http://localhost:4000/api/selectCategories').then((response)=>
    {
      setCategoryList(response.data);
    }
  )},[])

  return (
    <div>
        <hr></hr>
        <form>
  <p>Please select your preferred contact method:</p>
  <div>
    <input type="radio" id="contactChoice1"
     name="contact" value="email" />
    <label for="contactChoice1">Email</label>
</div>
<div>
    <input type="radio" id="contactChoice2"
     name="contact" value="phone" />
    <label for="contactChoice2">Phone</label>

    <input type="radio" id="contactChoice3"
     name="contact" value="mail" />
    <label for="contactChoice3">Mail</label>
  </div>
  <div>
    <button type="submit">Submit</button>
  </div>
</form>



<p>Please select your preferred contact method2:</p><form>
        {movieCategoriesList.map((val) =>{
            return (<div>
                <input type="radio" id={val.name}
                 name={val.name} value={val.name} />
                <label for={val.name}>{val.name}</label>
            </div>)

        })} <button type="submit">Submit</button></form>
  </div>)
}

export default CategoriesSelect
