import './App.css';
import React, {useEffect, useState} from "react";
import Axios from 'axios';

function Login() {
  
  const [username_log, setUsername_log] = useState("");
  const [password_log, setPassword_log] = useState("");

  const [loginStatus, setLoginStatus] = useState("");

  const loginuser = () => {
    Axios.post("http://localhost:4000/api/login", {
      username: username_log,
      password: password_log,
    }).then((response) => {
      console.log(response.data);
    });
};

  return (
    <div className="container page-content">
      <h4> Podaj dane:</h4>
      <hr></hr>
        <input type="text" placeholder="nazwa użytkownika" 
        name="username"
        onChange={(e) => { setUsername_log(e.target.value);
        }}
        ></input><br></br>

        <input type="text" placeholder="hasło" 
        name="password"
        onChange={(e) => 
          { setPassword_log(e.target.value);
        }}
        ></input><br></br>

        <button onClick={loginuser}> KLIKNIJ </button>

        <h1>{loginStatus}</h1>
    </div>
  );
}

export default Login;
