import React, {useEffect, useState} from "react";
import './App.css';
import Axios from 'axios';

function Categories() {

  const [name, setUsername] = useState("");
  const [description, setPassword] = useState("");
  const [icon, setEmail] = useState("");

  const [movieReviewList, setMovieList] = useState([]);

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/getUsers').then((response)=>
    {
      setMovieList(response.data);
    }
  )},[])

  const submitReview = () => {
      Axios.post("http://localhost:4000/api/insert", {
        username: username,
        password: password,
        email: email,
      }).then(() => {
        alert("successful insert");
      });
  };
  return (
    <div className="container page-content">
        <h4> Podaj dane:</h4>
        <hr></hr>
        <form method="post">
            <input type="text" placeholder="nazwa użytkownika" 
            name="username"
            onChange={(e) => { setUsername(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="hasło" 
            name="password"
            onChange={(e) => 
              { setPassword(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="email" 
            name="email"
            onChange={(e) => 
              { setEmail(e.target.value);
            }}
            ></input><br></br>

            <button onClick={submitReview}> KLIKNIJ </button>
            <h3> TAJNE DANE </h3>
            {movieReviewList.map((val) =>{
              return <p> Login: {val.username} | Hasło: {val.password} </p>
            })}
        </form>
    </div>
  );
}

export default Categories;
