import React, {useState,useEffect} from "react";
import '../App.css';
import Axios from 'axios';

function OfertSelectFromCategory() {

  const [CategoryData, setCategoryData] = useState([]);

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/selectOferts').then((response)=>
    {
      setCategoryData(response.data);
    }
  )},[])

  return (
    <div className="container page-content">
      <hr className="first"></hr>
      <h2> Nasze oferty </h2>
     
      {CategoryData.map((val) =>{
          return <div> <h3 key={val.title}>{val.title} </h3> <p key={val.description.toString()}><b>Opis produktu:</b><br></br> {val.description} </p><p key={val.price.toString()}><b>Cena:</b><br></br> {val.price} </p><p key={val.amount.toString()}><b>Ilość:</b><br></br> {val.amount} </p><p key={val.endDate.toString()}><b>Oferta trwa do:</b><br></br> {val.endDate} </p></div>  /* wersja zabezpieczona */
          // return <div><p> Login: <span dangerouslySetInnerHTML={{"__html": val.username}} /> | Hasło <span dangerouslySetInnerHTML={{"__html": val.password}} /></p></div>  /* wersja niezabezpieczona, kod HTML w dangerouslySetInnerHTML */
          // return <div><p><a href={val.username}>Nazwa użytkownika = {val.username}. Akapit przekierowuje do ścieżki twojego użytkownika </a></p></div>; /* wersja niezabezpieczona, kod javascript w atrybucie href znacznika a*/
        })}
      </div>)

}

export default OfertSelectFromCategory;
