import React, { useState} from "react";
import './App.css';
import Axios from 'axios';

function Categories() {

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [icon, setIcon] = useState("");

  // const [movieReviewList, setMovieList] = useState([]);

  // useEffect(()=> {
  //   Axios.get('http://localhost:4000/api/getUsers').then((response)=>
  //   {
  //     setMovieList(response.data);
  //   }
  // )},[])

  const submitReview = () => {
      Axios.post("http://localhost:4000/api/insertCategories", {
        name: name,
        description: description,
        icon: icon,
      }).then(() => {
        alert("successful insert category");
      });
  };
  return (
    <div className="container page-content">
        <h4> Podaj dane:</h4>
        <hr></hr>
        <form method="post">
            <input type="text" placeholder="nazwa kategorii" 
            name="name"
            onChange={(e) => { setName(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="opis" 
            name="description"
            onChange={(e) => 
              { setDescription(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="ikona font-awesome" 
            name="icon"
            onChange={(e) => 
              { setIcon(e.target.value);
            }}
            ></input><br></br>

            <button onClick={submitReview}> KLIKNIJ </button>
            <h3> TAJNE DANE </h3>
            {/* {movieReviewList.map((val) =>{
              return <p> Login: {val.username} | Hasło: {val.password} </p>
            })} */}
        </form>
    </div>
  );
}

export default Categories;
