import React, {useState,useEffect} from "react";
import '../App.css';
import Axios from 'axios';
import { useLocation } from "react-router-dom";
import queryString from 'query-string'

function OfertSelectFromCategory() {

  const {search} = useLocation()
  const searchParams = new URLSearchParams(search)
  const query = searchParams.get('searchID');
  const [searchID, setID] = useState("");

  const [CategoryData, setCategoryData] = useState([]);

  useEffect(()=> {
    Axios.get("http://localhost:4000/api/selectOfertsByID", {
      searchID: searchID,
    }).then((response)=>
    {
      
      setCategoryData(response.data);
    }
  )},[])

  const insertID = () => {
    Axios.post("http://localhost:4000/api/selectOfertsByID", {
      searchID: searchID,
    }).then(() => {
      alert("successful insert");
    });
};

  return (
    <div className="container page-content">
      <hr className="first"></hr>
      <h2> Nasze oferty </h2>
     
      <h3> Wyświetlasz oferty z kategorii o ID: <form><input type="text" name="searchID" value={query}/><input type="submit" value={insertID} > Wyślij </input></form> </h3>
      <h2>Wybierz kategorie</h2>
       <p> a {CategoryData.length} </p> 

      </div>)

}

export default OfertSelectFromCategory;
