import React, {Component, useState, useEffect} from "react";
import ReactDOM from 'react-dom';
import { Axios } from "axios";
import './index.css';

import{
BrowserRouter as Router,
Routes,
Route,
Link,
Redirect
} from "react-router-dom"

import OfertAdd from "./OfertAdd";
import Categories from "./Categories";
import Mainbar from "./Mainbar";
import Hello from "./Hello";
import Register from "./Register";
import Login from "./Login";
import Error from "./Error";
import CategoryOption from "./CategoryOption";
import Example from "./Example";
import NameForm from "./NameForm";

function Cat() {

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [icon, setIcon] = useState("");

  const [movieCategoriesList, setCategoryList] = useState([]);

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/getCategories').then((response)=>
    {
      setCategoryList(response.data);
    }
  )},[])

  const submitReviewCategories = () => {
      Axios.post("http://localhost:4000/api/insertCategories", {
        name: name,
        description: description,
        icon: icon,
      }).then(() => {
        alert("successful insert category");
      });
  };
  return (
    <div className="container page-content">
        <h4> Wpisz nowe kategorie:</h4>
        <hr></hr>
        <form method="post">
            <input type="text" placeholder="nazwa kategorii" 
            name="name"
            onChange={(e) => { setName(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="opis" 
            name="description"
            onChange={(e) => 
              { setDescription(e.target.value);
            }}
            ></input><br></br>

            <input type="text" placeholder="ikona font-awesome" 
            name="icon"
            onChange={(e) => 
              { setIcon(e.target.value);
            }}
            ></input><br></br>
        </form>
            <button onClick={submitReviewCategories}> KLIKNIJ </button>
            {movieCategoriesList.map((val) =>{
              return <div><h4><a href={val.name}>Kategoria: {val.name}</a></h4><p> Opis: {val.description} </p></div>
            })}

    </div>
  );
}

class App extends Component{render(){
  return(
  <Router>
    <Routes>
      <Route path='/' element={<div><Mainbar/><Hello login="studencie" /> <Categories /> <Register/> <Login /><OfertAdd /></div>} />
      <Route path={'/'+{movieCategoriesList}} element={<CategoryOption /> } />
      <Route path={'/login'} element={<Login /> } />
      <Route path='*' element={<Error /> } />
    </Routes>
    </Router>);}
}


ReactDOM.render(<App/>,document.getElementById('root'));
